import { useState } from 'react';

import "./ExpenseList.css"
import ExpenseItem from "./ExpenseItem"
import ExpensesFilter from "../ExpenseFilter/ExpenseFilter"
import Card from "../ui/Card"

const ExpenseList = (props) => {

    const [ chosenYear, setChosenYear ] = useState("2022")

    const filterChangeHandler = (selectedYear) => {
        console.log(selectedYear)
        setChosenYear(selectedYear)
    }

    const filteredExpenses = props.expenses.filter(expense => {
        return expense.date.getFullYear().toString() === chosenYear
    })

    let filterCondition = <p>No expenses found!</p>

    if (filteredExpenses.length > 0) {
        filterCondition = filteredExpenses.map(expense => 
            <ExpenseItem
                key = { expense.id }
                title = { expense.title }
                amount = { expense.amount }
                date = { expense.date }
            />
        )
    }

    return (

        <Card className="expenses">

            <ExpensesFilter selectedYear = { chosenYear } onChangeFilter = { filterChangeHandler } />

            {/* EXPENSE ITEMS */}

            { filterCondition }

            {/* old map method to display all expenses */}
            {/* { props.expenses.map(expense => 
                <ExpenseItem
                    key = { expense.id }
                    title = { expense.title }
                    amount = { expense.amount }
                    date = { expense.date }
                />
            )} */}

        </Card>
    )
}

export default ExpenseList
